import React, { Component } from 'react'
import {Line, Bar} from 'react-chartjs-2';


import returnsData from  '../assets/data/returns.json';
import indexData from  '../assets/data/index.json';
import performanceData from  '../assets/data/performance.json';
import cashData from  '../assets/data/cash.json';
import portfolioData from  '../assets/data/portfolio.json';

const indexColours = indexData.data.map((value) => value > 0 ? 'rgb(0, 204, 212)' : 'rgb(185, 153, 15)');

const datasetKeyProvider=()=>{ 
     return btoa(Math.random()).substring(0,12)
 } 


export class Dashboard extends Component { 

  state = {
        labels: returnsData[0].labels,
        datasets: [{
          label: 'Portfolio',
          data: returnsData[0].dataPortfolio,
          borderWidth: 2,
          fill: false,
          borderColor: ['rgb(0, 204, 212)'],
          pointHitRadius: 20
        },
        {
          label: 'S&P 500',
          data: returnsData[0].dataSP500,
          borderColor: ['rgb(185, 153, 15)'],
          backgroundColor: ['rgb(163, 134, 13)'],
          borderWidth: 1,
          fill: false,
          pointHitRadius: 20
        }],
        subtitle: returnsData[0].subtitle    
       };


  periodChange = (x) => {
    console.log(x);
    var ChartData = {
        labels: returnsData[x].labels,
        datasets: [{
          label: 'Portfolio',
          data: returnsData[x].dataPortfolio,
          borderWidth: 2,
          fill: false,
          borderColor: ['rgb(0, 204, 212)'],
          pointHitRadius: 20
        },
        {
          label: 'S&P 500',
          data: returnsData[x].dataSP500,
          borderColor: ['rgb(185, 153, 15)'],
          backgroundColor: ['rgb(163, 134, 13)'],
          borderWidth: 1,
          fill: false,
          pointHitRadius: 20

        }],
        subtitle: returnsData[x].subtitle    
       };
    this.setState(ChartData);
  }

  returnsChartOptions = {
    scales: {
      xAxes: [{
        gridLines: {
          display: false,
          lineWidth: 100,
          borderColor: ['rgb(148, 151, 193)'],
        }
      }],
      yAxes: [{
        ticks: {
          callback: function(value, index, values) {
            return Math.round(value * 1000) / 10 + "%"
          },
        fontColor: 'rgb(148, 151, 193)'
        },
        gridLines: {
          drawBorder: true,
          borderDash: [1,3],

          zeroLineColor: 'rgba(148, 151, 193,.3)'
        }
      }]
    },
    legend: {
      display: true,
      position: 'top'
    },
    elements: {
      point: {
        radius: 0
      },
      line: {
        tension: 0
      }
    },
    tooltips: {
      callbacks: {
        label: function(tooltipItem, data) {
          var dataset = data.datasets[tooltipItem.datasetIndex];
          var currentValue = dataset.data[tooltipItem.index];
          var percentage = parseFloat((currentValue*100).toFixed(1));
          return percentage + '%';
        },
        title: function(tooltipItem, data) {
          return data.labels[tooltipItem[0].index];
        }
      },

    },
    maintainAspectRatio: false,zoom: {
     enabled: true,
     mode: 'x',
   },
   pan: {
      enabled: true,
      mode: 'x',
    },
  };


  indexChartData = {
    labels: indexData.labels,
    datasets: [{
      label: 'Portfolio',
      data: indexData.data,
      borderWidth: 1,
      fill: false,
      borderColor: indexColours,
      backgroundColor: indexColours,
    }]
  };
  indexChartOptions = {
    scales: {
      xAxes: [{
        gridLines: {
          display: false,
          lineWidth: 100,
          borderColor: ['rgb(148, 151, 193)'],
        }
      }],
      yAxes: [{
        ticks: {
          callback: function(value, index, values) {
            return Math.round((value +1) *100)
          },
        fontColor: 'rgb(148, 151, 193)'
        },
        gridLines: {
          drawBorder: true,
          borderDash: [1,3],

          zeroLineColor: 'rgba(148, 151, 193,.3)'
        }
      }]
    },
    legend: {
      display: false,
    },
    elements: {
      point: {
        radius: 0
      },
      line: {
        tension: 0
      }
    },
    tooltips: {
      callbacks: {
        label: function(tooltipItem, data) {
          var dataset = data.datasets[tooltipItem.datasetIndex];
          var currentValue = dataset.data[tooltipItem.index];
          var index = parseFloat(((currentValue +1)*100 ).toFixed(1));
          return index;
        },
        title: function(tooltipItem, data) {
          return data.labels[tooltipItem[0].index];
        }
      },

    },
    maintainAspectRatio: false
  };


  PerformanceChartData = {
    labels: performanceData.labels,
    datasets: [{
      label: 'S&P500',
      data: performanceData.sp500,
      fill: true,
      backgroundColor: ['rgba(20, 22, 49, 1)'],
      borderColor: ['rgb(163, 134, 13)'],
      borderWidth: 1,
      pointHitRadius: 20
    },{
      label: 'Portfolio',
      data: performanceData.portfolio,
      borderWidth: 2,
      fill: true,
      backgroundColor: ['rgba(0, 204, 212, .2)'],
      borderColor: ['rgb(0, 204, 212)'],
      pointHitRadius: 20
    }]
  };

  PerformanceChartOptions = {
    scales: {
      yAxes: [{
        display: true,
        gridLines: {
          drawBorder: false,
          display: false,
          drawTicks: false
        },
        ticks: {
          display: false
        }
      }],
      xAxes: [{
        display: false,
        position: 'bottom',
        gridLines: {
          drawBorder: false,
          display: false,
          drawTicks: false,
        },
        ticks: {
          beginAtZero: false,
          stepSize: 10,
          fontColor: "#a7afb7",
          padding: 10,
        }
      }],
    },
    legend: {
      display: false,
      position: 'top'
    },
    elements: {
      point: {
        radius: 0
      },
      line: {
        tension: 0
      }
    },
    tooltips: {
      callbacks: {
        label: function(tooltipItem, data) {
          var dataset = data.datasets[tooltipItem.datasetIndex];
          var currentValue = dataset.data[tooltipItem.index];
          var value = parseFloat((currentValue).toFixed(2));
          return value;
        },
        title: function(tooltipItem, data) {
          return data.labels[tooltipItem[0].index];
        }
      }
    },
    maintainAspectRatio: false
  };

  feesChartData = {
    labels: cashData.fees_labels,
    datasets: [{
      data: cashData.fees_data.slice(0, cashData.fees_data.length - 1).concat([undefined]),
      borderWidth: 2,
      fill: true,
      backgroundColor: 'rgba(0, 204, 212, .2)',
      borderColor: 'rgb(0, 204, 212)',
      pointHitRadius: 20
    },
    {
      data: Array(cashData.fees_data.length - 2 ).fill(undefined).concat( cashData.fees_data.slice(cashData.fees_data.length - 2, cashData.fees_data.length)),
      borderWidth: 2,
      fill: false,
      borderColor: 'rgb(0, 204, 212)',
      borderDash: [1,3],
      pointHitRadius: 20
    }],

  };

  feesChartOptions = {
    scales: {
      yAxes: [{
        display: false,
        gridLines: {
          drawBorder: false,
          display: false,
          drawTicks: false,
        },
        ticks: {
          display: false,
          beginAtZero: true,
          min: 0,

        }
      }],
      xAxes: [{
        display: false,
        position: 'bottom',
        gridLines: {
          drawBorder: false,
          display: false,
          drawTicks: false,
        },
        ticks: {
          beginAtZero: true,
          stepSize: 10,
          fontColor: "#a7afb7",
          padding: 10,
        }
      }],
    },
    legend: {
      display: false,
    },
    elements: {
      point: {
        radius: 0
      },
      line: {
        tension: 0
      }
    },
    tooltips: {
      callbacks: {
        label: function(tooltipItem, data) {
          var dataset = data.datasets[tooltipItem.datasetIndex];
          var currentValue = dataset.data[tooltipItem.index];
          var value = parseFloat((currentValue).toFixed(2));
          return "\u20AC " + value;
